import React, { useState, useEffect, useRef } from "react";
import "./ChatWindow.css";
import { getAIMessage } from "../api/api";
import { marked } from "marked";

function ChatWindow() {

  const defaultMessage = [{
    role: "assistant",
    content: "Hi, how can I help you today?"
  }];

  const [messages,setMessages] = useState(defaultMessage)
  const [input, setInput] = useState("");

  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
      messagesEndRef.current.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
      scrollToBottom();
  }, [messages]);

  // const handleSend = async (input) => {
  //   console.log("input is" + input)
  //   if (input.trim() !== "") {
  //     // Set user message
  //     setMessages(prevMessages => [...prevMessages, { role: "user", content: input }]);
  //     setInput("");

  //     // Call API & set assistant message
  //     const newMessage = await getAIMessage(input);
  //     setMessages(prevMessages => [...prevMessages, newMessage]);
  //   }
  // };

  // const handleSend = async (input) => {
  //   if (input.trim() !== "") {
  //     const newUserMessage = { role: "user", content: input };
  //     const updatedMessages = [...messages, newUserMessage];
  //     setMessages(updatedMessages);
  //     setInput("");

  //     const newMessage = await getAIMessage(input, updatedMessages);
  //     setMessages([...updatedMessages, newMessage]);
  //   }
  // };

  const handleSend = async (input) => {
    if (input.trim() !== "") {
      const newUserMessage = { role: "user", content: input };
      const updatedMessages = [...messages, newUserMessage];
      setMessages(updatedMessages);
      setInput("");

      let assistantMsg = { role: "assistant", content: "" };
      setMessages((prev) => [...prev, assistantMsg]);

      await getAIMessageStream(input, updatedMessages, (streamedText) => {
        assistantMsg.content = streamedText;
        setMessages((prev) => [...prev.slice(0, -1), { ...assistantMsg }]);
      });
    }
  };



  return (
      <div className="messages-container">
          {messages.map((message, index) => (
              <div key={index} className={`${message.role}-message-container`}>
                  {message.content && (
                      <div className={`message ${message.role}-message`}>
                          {/* <div dangerouslySetInnerHTML={{__html: marked(message.content).replace(/<p>|<\/p>/g, "")}}></div> */}
                          <div
  dangerouslySetInnerHTML={{
    __html: marked(
      Array.isArray(message.content) ? message.content.join("\n\n") : message.content
    ).replace(/<p>|<\/p>/g, ""),
  }}
></div>

                      </div>
                  )}
              </div>
          ))}
          <div ref={messagesEndRef} />
          <button
  className="reset-button"
  onClick={() => setMessages([{
    role: "assistant",
    content: "Hi, how can I help you today?"
  }])}
>
  🔁 Reset Chat
</button>

<button
  className="summarize-button"
  onClick={async () => {
    setInput("Summarize our conversation so far")
    const res = await getAIMessage("Summarize our conversation so far", messages);
    setMessages(prev => [...prev, { role: "user", content: "Summarize our conversation so far" }, res]);
  }}
>
  📋 Summarize
</button>


          <div className="input-area">
            <input
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type a message..."
              onKeyPress={(e) => {
                if (e.key === "Enter" && !e.shiftKey) {
                  handleSend(input);
                  e.preventDefault();
                }
              }}
              rows="3"
            />
           <button className="send-button" onClick={() => handleSend(input)}>
  Send
</button>

          </div>
      </div>
);
}

export default ChatWindow;
