import React, { useState, useEffect, useRef } from "react";
import "./ChatWindow.css";
import { getAIMessage, getAIMessageStream } from "../api/api";
import { marked } from "marked";
import PartSelectLogo from "../PartSelectLogo.svg"; 

function ChatWindow() {
  const defaultMessage = [
    {
      role: "assistant",
      content:
        "👋 Welcome to **PartSelect Assistant**! I can help you install parts, check compatibility, and troubleshoot appliances. Just ask away!",
    },
  ];

  const [messages, setMessages] = useState(defaultMessage);
  const [input, setInput] = useState("");
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = async (input) => {
    if (input.trim() === "") return;

    const newUserMessage = { role: "user", content: input };
    const updatedMessages = [...messages, newUserMessage];
    setMessages(updatedMessages);
    setInput("");

    let assistantMsg = { role: "assistant", content: "" };
    setMessages((prev) => [...prev, assistantMsg]);

    await getAIMessageStream(input, updatedMessages, (streamedText) => {
      assistantMsg.content = streamedText;
      setMessages((prev) => [...prev.slice(0, -1), { ...assistantMsg }]);
    });
  };

  return (
    <div className="chat-wrapper">
      <div className="chat-header">
        <img src={PartSelectLogo} alt="PartSelect Logo" className="logo" />
        <span>PartSelect Assistant</span>
      </div>

      <div className="messages-container">
        {messages.map((message, index) => (
          <div key={index} className={`${message.role}-message-container`}>
            {message.content && (
              <div className={`message ${message.role}-message`}>
                <div
                  dangerouslySetInnerHTML={{
                    __html: marked(
                      Array.isArray(message.content)
                        ? message.content.join("\n\n")
                        : message.content
                    ).replace(/<p>|<\/p>/g, ""),
                  }}
                ></div>
              </div>
            )}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div className="control-buttons">
        <button
          className="reset-button"
          onClick={() =>
            setMessages([
              {
                role: "assistant",
                content:
                  "👋 Welcome to **PartSelect Assistant**! Ask me about installation, compatibility, or troubleshooting.",
              },
            ])
          }
        >
          🔁 Reset
        </button>

        <button
          className="summarize-button"
          onClick={async () => {
            const summarizeMsg = "Summarize our conversation so far";
            setInput(summarizeMsg);
            const res = await getAIMessage(summarizeMsg, messages);
            setMessages((prev) => [
              ...prev,
              { role: "user", content: summarizeMsg },
              res,
            ]);
          }}
        >
          📋 Summary
        </button>
      </div>

      <div className="input-area">
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask a question about an appliance part..."
          onKeyPress={(e) => {
            if (e.key === "Enter" && !e.shiftKey) {
              handleSend(input);
              e.preventDefault();
            }
          }}
        />
        <button className="send-button" onClick={() => handleSend(input)}>
          Send
        </button>
      </div>
    </div>
  );
}

export default ChatWindow;
